import React, { useState, useEffect } from 'react';
import { View, Text, Button } from 'react-native';
import { ListItem } from 'react-native-elements';
import { CATEGORIES } from '../data';

const Categories = (props) => {
  const { handleCategoryChanges } = props;
  const [categories, setCategories] = useState([]);
  const [chosenCategories, setChosenCategories] = useState([]);
  useEffect(() => {
    setCategories(CATEGORIES);
  }, []);

  return (
    <View>
      <Text>Kategorien</Text>
      {categories.map((category) => (
        <ListItem
          containerStyle={category?.selected ? selectedStyle : undefined}
          key={category.id}
          bottomDivider
          onPress={() =>
            setCategories([...categories, { ...category, selected: true }])
          }
        >
          <ListItem.Content>
            <ListItem.Title>{category.title}</ListItem.Title>
            <ListItem.Subtitle>{category.desc}</ListItem.Subtitle>
          </ListItem.Content>
        </ListItem>
      ))}
      <Button
        title="Kategorien auswÃ¤hlen"
        onPress={() => handleCategoryChanges(categories)}
      />
    </View>
  );
};

const selectedStyle = {
  backgroundColor: 'gray',
};

export default Categories;
