import React, { useState, useEffect } from 'react';
import { View, Text, Button } from 'react-native';
import { ListItem } from 'react-native-elements';
import { CATEGORIES } from '../data';

const Categories = (props) => {
  const { handleCategoryChanges } = props;
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    setCategories(CATEGORIES);
  }, []);

  const handleOnPress = (category) => {
      const selectCategory = categories.map(cat => {
          if (!category.selected) {
              return category
          }
      })
  };

  const saveCategories = () => {
    const selectedCategories = categories.filter((cat) => !!cat.selected);
    handleCategoryChanges(selectedCategories);
  };

  return (
    <View>
      <Text>Kategorien</Text>
      {categories.map((category) => (
        <ListItem
          containerStyle={category?.selected ? selectedStyle : undefined}
          key={category.id}
          bottomDivider
          onPress={() => handleOnPress(category)}
        >
          <ListItem.Content>
            <ListItem.Title>{category.title}</ListItem.Title>
            <ListItem.Subtitle>{category.desc}</ListItem.Subtitle>
          </ListItem.Content>
        </ListItem>
      ))}
      <Button title="Kategorien auswÃ¤hlen" onPress={saveCategories} />
    </View>
  );
};

const selectedStyle = {
  backgroundColor: 'gray',
};

export default Categories;
